import React from 'react';
import './Button.css';

const Button04 = ({ text = "Nothing-Plop" }) => {
  // Define dot indices for the two icon variations
  const firstIconDots = [0, 2, 2, 1, 2, 0, 1, 1, 2, 2, 0, 1, 0, 2, 2, 1, 0, 2, 2, 2, 2, 0, 1, 0, 2];
  const secondIconDots = [0, 2, 2, 1, 2, 0, 1, 1, 2];

  return (
    <a href="#" className="button04 w-inline-block">
      <span className="button04_bg"></span>
      <span data-text={text} className="button04_inner">
        <span className="button04_text">{text}</span>
        <span className="button04_icon-wrap">
          <span 
            style={{ '--index-parent': 0 } as React.CSSProperties} 
            className="button04_icon"
          >
            {firstIconDots.map((index, i) => (
              <span
                key={`first-dot-${i}`}
                style={{ '--index': index }as React.CSSProperties}
                className="button04_dot"
              ></span>
            ))}
          </span>
          <span 
            style={{ '--index-parent': 1 }as React.CSSProperties} 
            className="button04_icon is-arrow"
          >
            {secondIconDots.map((index, i) => (
              <span
                key={`second-dot-${i}`}
                style={{ '--index': index }as React.CSSProperties}
                className="button04_dot"
              ></span>
            ))}
          </span>
        </span>
      </span>
    </a>
  );
};

export default Button04;

.button04 {
  color: #f9f4eb;
  letter-spacing: -.02em;
  text-transform: uppercase;
  -webkit-user-select: none;
  user-select: none;
  transform-style: preserve-3d;
  font-size: .875rem;
  line-height: 1em;
  text-decoration: none;
  display: grid
}

.button04_bg {
  background-color: #000;
  border-radius: .3125rem;
  grid-area: 1/1;
  overflow: clip
}

.button04_inner {
  grid-gap: .625rem;
  grid-area: 1/1;
  padding: .875rem .625rem .875rem .75rem;
  display: grid;
  translate: 0
}

.button04_text {
  grid-area: 1/1
}

.button04_icon-wrap {
  grid-row: 1;
  display: grid
}

.button04_icon {
  grid-gap: .0625rem;
  grid-area: 1/1;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  display: grid
}

.button04_dot {
  background-color: #f9f4eb;
  border-radius: .0625rem;
  width: .125rem;
  height: .125rem;
  display: block
}

.button04 {
  --elastic-ease-out: linear(
    0,
    0.5737 7.6%,
    0.8382 11.87%,
    0.9463 14.19%,
    1.0292 16.54%,
    1.0886 18.97%,
    1.1258 21.53%,
    1.137 22.97%,
    1.1424 24.48%,
    1.1423 26.1%,
    1.1366 27.86%,
    1.1165 31.01%,
    1.0507 38.62%,
    1.0219 42.57%,
    0.9995 46.99%,
    0.9872 51.63%,
    0.9842 58.77%,
    1.0011 81.26%,
    1
  );
  --smooth-ease: cubic-bezier(.32, .72, 0, 1);
  --color-ease: cubic-bezier(.215, .61, .355, 1);
  
  transition: transform .45s var(--elastic-ease-out);
  -webkit-tap-highlight-color: transparent;
}

.button04:active {
	transform: scaleX(0.955) scaleY(0.954);
}

.button04_bg {
	transition: scale .45s var(--smooth-ease), background-color .2s var(--color-ease);
}

.button04_dot {
	transition: scale .4s var(--smooth-ease);
  transition-delay: calc(var(--index) * .04s);
}

.button04_icon.is-arrow .button04_dot {
	scale: 0;
  transition-delay: 0s;
}

.button04_text {
	transition: scale .45s var(--smooth-ease);
  transform-origin: center top;
}

.button04_inner::before {
	content: attr(data-text);
  grid-area: 1 / 1;
  scale: 1 0;
  pointer-events: none;
  transition: scale .45s var(--smooth-ease);
  transform-origin: center bottom;
}

/* dot positioning like an arrow, there is no native nth-child inside Webflow */
.button04_icon.is-arrow .button04_dot:nth-child(1) {
	grid-area: 3 / 1;
}

.button04_icon.is-arrow .button04_dot:nth-child(2) {
	grid-area: 3 / 2;
}

.button04_icon.is-arrow .button04_dot:nth-child(3) {
	grid-area: 3 / 3;
}

.button04_icon.is-arrow .button04_dot:nth-child(4) {
	grid-area: 3 / 4;
}

.button04_icon.is-arrow .button04_dot:nth-child(5) {
	grid-area: 3 / 5;
}

.button04_icon.is-arrow .button04_dot:nth-child(6) {
	grid-area: 2 / 4;
}

.button04_icon.is-arrow .button04_dot:nth-child(7) {
	grid-area: 1 / 3;
}

.button04_icon.is-arrow .button04_dot:nth-child(8) {
	grid-area: 4 / 4;
}

.button04_icon.is-arrow .button04_dot:nth-child(9) {
	grid-area: 5 / 3;
}

@media (hover: hover) and (pointer: fine) {
  .button04:is(:hover, :focus-visible) .button04_text {
  	scale: 1 0;
  }
  
  .button04:is(:hover, :focus-visible) .button04_inner::before {
  	scale: 1 1;
  }
  
  .button04:is(:hover, :focus-visible) .button04_dot {
  	scale: 0;
    transition-delay: calc((var(--index) * .04s) + (var(--index-parent) * .07s));
  }
  
  .button04:is(:hover, :focus-visible) .button04_icon.is-arrow .button04_dot {
  	scale: 1;
  }
}

/* style only for touch devices */
@media (hover: none) or (pointer: coarse) {
  .button04:active .button04_bg {
  	background-color: #4D4D4D;
    transition-duration: 0s;
  }
  
  .button04_dot {
  	scale: 0;
  }
  
  .button04_icon.is-arrow .button04_dot {
  	scale: 1;
  }
}
