.button06_icon-svg {
  height: 1rem
}

.button06_icon {
  pointer-events: none;
  color: #000;
  grid-area: 1/1;
  place-self: center flex-end;
  height: 1rem;
  display: flex;
  transform: translate(calc(100% + .375rem))
}

.button06_bg {
  pointer-events: none;
  background-color: #000;
  border-radius: .3125rem;
  grid-area: 1/1;
  overflow: clip
}

.button06_icon-mid {
  transform-origin: 0%;
  background-color: currentColor;
  flex: none;
  width: .0625rem
}

.button06 {
  color: #f9f4eb;
  letter-spacing: -.02em;
  text-transform: uppercase;
  -webkit-user-select: none;
  user-select: none;
  transform-style: preserve-3d;
  font-size: .875rem;
  line-height: 1em;
  text-decoration: none;
  display: grid
}

.button06_icon-end {
  flex: none;
  margin-left: -.03125rem
}

.button06_icon-start {
  flex: none;
  margin-right: -.03125rem
}

.button06_inner {
  grid-column-gap: .625rem;
  grid-row-gap: .625rem;
  grid-area: 1/1;
  padding: .875rem 1rem;
  display: grid;
  translate: 0
}

.button06_text {
  grid-area: 1/1;
  display: block
}
.button06 {
  --elastic-ease-out: linear(
    0,
    0.5737 7.6%,
    0.8382 11.87%,
    0.9463 14.19%,
    1.0292 16.54%,
    1.0886 18.97%,
    1.1258 21.53%,
    1.137 22.97%,
    1.1424 24.48%,
    1.1423 26.1%,
    1.1366 27.86%,
    1.1165 31.01%,
    1.0507 38.62%,
    1.0219 42.57%,
    0.9995 46.99%,
    0.9872 51.63%,
    0.9842 58.77%,
    1.0011 81.26%,
    1
  );
  --animation-ease: cubic-bezier(.65, .06, .33, .89);
  --smooth-ease: cubic-bezier(.32, .72, 0, 1);
  --color-ease: cubic-bezier(.215, .61, .355, 1);
  
  transition: transform .65s var(--elastic-ease-out);
  -webkit-tap-highlight-color: transparent;
}

.button06:active {
	transform: scaleX(0.955) scaleY(0.954);
}

.button06_bg {
	transition: scale .75s var(--elastic-ease-out), background-color .2s var(--color-ease);
}

.button06_icon {
	transition: translate .85s var(--elastic-ease-out), opacity .15s ease-out;
  translate: 3rem 0 0;
  opacity: 0;
  animation: right .6s var(--animation-ease) none infinite;
  animation-play-state: running;
}

.button06_icon-mid {
	transition: transform .375s var(--smooth-ease);
  transform: scaleX(33);
}

.button06_icon-end {
	transition: transform .375s var(--smooth-ease);
  transform: translateX(2rem);
}

.button06_inner::before {
	content: attr(data-text);
  grid-area: 1 / 1;
  scale: 0 1;
  pointer-events: none;
  transition: scale .45s var(--smooth-ease);
  transform-origin: right center;
}

.button06_text {
	transition: scale .45s var(--smooth-ease);
  transform-origin: left center;
}

@media (hover: hover) and (pointer: fine) {
	.button06:is(:hover, :focus-visible) .button06_icon {
  	translate: 0 0 0;
    transition: translate .85s var(--elastic-ease-out), opacity .025s ease-in;
    opacity: 1;
  }
  
  .button06:is(:hover, :focus-visible) .button06_icon-mid {
  	transform: scaleX(1);
  }
  
  .button06:is(:hover, :focus-visible) .button06_icon-end {
  	transform: translateX(0);
  }
  
  .button06:is(:hover, :focus-visible) .button06_bg {
  	scale: .955 .954;
    transition-delay: .05s;
  }
  
  .button06_icon {
    animation: none;
  }
  
  .button06:is(:hover, :focus-visible) .button06_icon {
    animation: right .85s var(--animation-ease) none infinite;
    animation-delay: .25s;
  }
  
  .button06:is(:hover, :focus-visible) .button06_text {
    scale: 0 1;
    transition-delay: .07s;
  }
  
  .button06:is(:hover, :focus-visible) .button06_inner::before {
    scale: 1 1;
    transition-delay: .07s;
  }
}

/* style only for touch devices */
@media (hover: none) or (pointer: coarse) {
	.button06:active .button06_bg {
  	background-color: #4D4D4D;
    transition-duration: 0s;
  }
  
  .button06_icon {
    animation: right .85s var(--animation-ease) none infinite;
    animation-delay: 0s;
  }
  
  .button06_icon {
  	translate: 0 0 0;
    opacity: 1;
  }
  
  .button06_icon-mid {
  	transform: scaleX(1);
  }
  
  .button06_icon-end {
  	transform: translateX(0);
  }
}

@keyframes right {
	0% {
    transform: translateX(calc(100% + .375rem));
	}
  50% {
  	transform: translateX(calc(100% + .375rem + 0.25rem));
  }
	100% {
		transform: translateX(calc(100% + .375rem));
	}
}
